import {
  useQuery,
  QueryClient,
  QueryClientProvider,
} from "@tanstack/react-query";
import axios from "axios";
import { useState } from "react";

interface WeatherData {
  hourly: {
    temperature_2m: number;
  };
}

export const fetchWeather = async () => {
  const response = await axios.get<WeatherData>(
    "https://api.open-meteo.com/v1/forecast",
    {
      params: {
        latitude: 52.4069,
        longitude: 16.9299,
        hourly: ["temperature_2m", "rain"],
      },
    }
  );
  return response.data;
};

const queryClient = new QueryClient();

function currentWeather() {
  const currentHour = new Date().getHours();
  const { data, error, isLoading } = useQuery<WeatherData, Error>({
    queryKey: ["weather"],
    queryFn: fetchWeather,
    refetchInterval: 1000,
  });
  if (isLoading) return <p>Ładowanie danych</p>;
  if (error) return <p>Błąd: {error.message}</p>;
  const currentTemperature = data?.hourly.temperature_2m[currentHour];
  return (
    <>
      <div className="p-8 bg-gray-200 basis-1/4 shadow-xl rounded-lg flex flex-row">
        <div className="p-8 bg-gray-300 basis-1/4 shadow-xl rounded-lg">
          Aktualna prognoza pogoda dla Poznania
        </div>
        <div className="p-8 basis-1/4">02</div>
        <div className="p-8 basis-1/2">03</div>
      </div>
    </>
  );
}

const Weather: React.FC = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Weather />
    </QueryClientProvider>
  );
};

export default Weather;
