import React from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/PublicaMundi/MappingAPI/master/data/geojson/world-countries.geojson"; // Zaktualizowany link

const Map = () => {
  return (
    <ComposableMap style={{ width: "100%", height: "auto" }}>
      <Geographies geography={geoUrl}>
        {({ geographies }) => {
          if (!geographies.length) {
            return <p>Loading...</p>; // Komunikat ładowania
          }
          return geographies.map((geo) => (
            <Geography key={geo.properties.ADMIN} geography={geo} />
          ));
        }}
      </Geographies>
    </ComposableMap>
  );
};

export default Map;
