import {
  useQuery,
  QueryClient,
  QueryClientProvider,
} from "@tanstack/react-query";
import axios from "axios";
import { useState } from "react";

interface WeatherData {
  hourly: {
    temperature_2m: number;
  };
}

export const fetchWeather = async () => {
  const response = await axios.get<WeatherData>(
    "https://api.open-meteo.com/v1/forecast",
    {
      params: {
        latitude: 52.4069,
        longitude: 16.9299,
        hourly: ["temperature_2m", "rain"],
      },
    }
  );
};

const Weather: React.FC = () => {
  return (
    <>
      <div className="p-8 bg-gray-200 basis-1/4 shadow-xl rounded-lg flex flex-row">
        <div className="p-8 bg-gray-300 basis-1/4 shadow-xl rounded-lg">
          Aktualna prognoza pogoda dla Poznania
        </div>
        <div className="p-8 basis-1/4">02</div>
        <div className="p-8 basis-1/2">03</div>
      </div>
    </>
  );
};

export default Weather;
