import {
  useQuery,
  QueryClient,
  QueryClientProvider,
} from "@tanstack/react-query";
import axios from "axios";
import { useState } from "react";
const latitude: number = 52.4069;
const longitude: number = 16.9299;

interface WeatherData {
  hourly: {
    temperature_2m: number[];
  };
}

export const fetchWeather = async () => {
  const response = await axios.get<WeatherData>(
    "https://api.open-meteo.com/v1/forecast",
    {
      params: {
        latitude: { latitude },
        longitude: { longitude },
        hourly: ["temperature_2m", "rain"],
      },
    }
  );
  return response.data;
};

const queryClient = new QueryClient();

function CurrentWeather() {
  const currentHour = new Date().getHours();
  const { data, error, isLoading } = useQuery<WeatherData, Error>({
    queryKey: ["weather"],
    queryFn: fetchWeather,
    refetchInterval: 1000,
  });
  if (isLoading) return <p>Ładowanie danych</p>;
  if (error) return <p>Błąd: {error.message}</p>;
  const currentTemperature = data?.hourly.temperature_2m[currentHour];

  return (
    <div className="p-8 bg-gray-200 basis-1/4 shadow-xl rounded-lg flex flex-col items-center">
      <h2 className="text-xl font-bold">
        Aktualna prognoza pogody dla Poznania
      </h2>
      <div className="text-4xl my-4">
        {currentTemperature !== undefined
          ? `${currentTemperature}°C`
          : "Brak danych"}
      </div>
    </div>
  );
}

const Weather: React.FC = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <CurrentWeather />
    </QueryClientProvider>
  );
};

export default Weather;
