// src/components/MapComponent.tsx
import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, GeoJSON, useMap } from "react-leaflet";
import countryData from "./countries.geojson"; // Upewnij się, że ścieżka do pliku GeoJSON jest poprawna
import "leaflet/dist/leaflet.css";

interface MapComponentProps {
  country: string;
}

const ChangeView: React.FC<{ center: [number, number]; zoom: number }> = ({
  center,
  zoom,
}) => {
  const map = useMap();
  useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);

  return null;
};

const MapComponent: React.FC<MapComponentProps> = ({ country }) => {
  const [selectedCountry, setSelectedCountry] = useState<any | null>(null);

  useEffect(() => {
    // Znajdź dane GeoJSON wybranego kraju
    const countryFeature = countryData.features.find(
      (feature: any) => feature.properties.name === country
    );
    setSelectedCountry(countryFeature || null);
  }, [country]);

  const highlightStyle = {
    fillColor: "blue",
    weight: 2,
    opacity: 1,
    color: "blue",
    fillOpacity: 0.3,
  };

  const defaultCenter: [number, number] = [51.505, -0.09]; // Domyślne centrum mapy
  const zoomLevel = 4; // Domyślny poziom powiększenia

  return (
    <MapContainer
      center={defaultCenter}
      zoom={zoomLevel}
      style={{ height: "400px", width: "100%" }}
    >
      <ChangeView center={defaultCenter} zoom={zoomLevel} />
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
      />
      {selectedCountry && (
        <GeoJSON
          data={selectedCountry}
          pathOptions={highlightStyle} // Zmiana z `style` na `pathOptions`
        />
      )}
      <GeoJSON
        data={countryData}
        pathOptions={{
          fillColor: "gray",
          weight: 1,
          color: "black",
          fillOpacity: 0.1,
        }} // Zmiana z `style` na `pathOptions`
      />
    </MapContainer>
  );
};

export default MapComponent;
